package greatestcommondivisor

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestGcdOfStrings(t *testing.T) {
	result := gcdOfStrings("ABCABC", "ABC")
	require.Equal(t, "ABC", result)

	result = gcdOfStrings("ABABAB", "ABAB")
	require.Equal(t, "AB", result)

	result = gcdOfStrings("LEET", "CODE")
	require.Equal(t, "", result)

	result = gcdOfStrings("TAUXXTAUXXTAUXXTAUXXTAUXX", "TAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXX")
	require.Equal(t, "TAUXX", result)

	result = gcdOfStrings("",
		"")
	require.Equal(t, "", result)
}
